import { findClosestNumber } from "./find-closest-number";

describe("closest number", () => {
  describe("codingame tests", () => {
    test("M has fewer digits", () => {
      const N = "94754";
      const M = "3841";

      const expected = findClosestNumber(N, M);

      const actual = "8431";

      expect(expected).toBe(actual);
    });

    test("M has more digits", () => {
      const N = "2749";
      const M = "284512";

      const expected = findClosestNumber(N, M);

      const actual = "122458";

      expect(expected).toBe(actual);
    });

    test("Exact solution", () => {
      const N = "123";
      const M = "231";

      const expected = findClosestNumber(N, M);

      const actual = "123";

      expect(expected).toBe(actual);
    });

    test("Solution with leading zeros", () => {
      const N = "111";
      const M = "3204";

      const expected = findClosestNumber(N, M);

      const actual = "234";

      expect(expected).toBe(actual);
    });

    test("Same number of digits, solution < N", () => {
      const N = "45284";
      const M = "17404";

      const expected = findClosestNumber(N, M);

      const actual = "44710";

      expect(expected).toBe(actual);
    });

    test("Same number of digits, solution > N", () => {
      const N = "82738";
      const M = "33898";

      const expected = findClosestNumber(N, M);

      const actual = "83389";

      expect(expected).toBe(actual);
    });

    test("No permutation greater than N", () => {
      const N = "93583";
      const M = "33428";

      const expected = findClosestNumber(N, M);

      const actual = "84332";

      expect(expected).toBe(actual);
    });

    test("Longest common prefix is not always right", () => {
      const N = "590";
      const M = "506";

      const expected = findClosestNumber(N, M);

      const actual = "605";

      expect(expected).toBe(actual);
    });

    test("Two optimal permutations", () => {
      const N = "77";
      const M = "95";

      const expected = findClosestNumber(N, M);

      const actual = "59";

      expect(expected).toBe(actual);
    });

    test("M has more digits, with zeros", () => {
      const N = "8732";
      const M = "4000007";

      const expected = findClosestNumber(N, M);

      const actual = "7400";

      expect(expected).toBe(actual);
    });

    test("Very large numbers 3", () => {
      const N =
        "";
      const M =
        "";

      const expected = findClosestNumber(N, M);

      const actual =
        "";

      expect(expected).toBe(actual);
    });
  });
});
